edor.contenedor = ctcontenedor.id AND partida_contenedor.partida = ctpartida.id  GROUP By ctdua.numero";         $sql="SELECT ctdua.numero, round(sum(ctcontenedor.total),2) as suma FROM ctdua, ctcontenedor, ctpartida WHERE ctpartida.dua = ctdua.id_dua AND ctdua.entrada_salida = 'sa' AND ctcontenedor.partida = ctpartida.id  GROUP By ctdua.numero";   	$dades=mysql_query($sql, $conexion) or die("error : ".mysql_error() );   ?>   <table id="example">   	<thead>   		<th  width="200">Nº Dua Salida</td><th width="200">Total</td>   	</thead>   	<tr>   	   	<?php   	$total_sa = 0;   	while($row=mysql_fetch_array($dades))	{		echo "<tr>";		echo "<td >".$row['numero']."</td><td>".$row['suma']."€</td>";		$total_sa += $row['suma'];		echo "</tr>";	}	?>	   	</tr>   </table>   Total: <?php echo $total_sa; ?>€      </div>       	<div style="margin-top: 30px;">DEUDA POTENCIAL: <?php echo $total_en - $total_sa ?>€</div>    	<div >IMPORTE DE GARANTIA: 100.000€</div>    	<?php    	if(($total_en - $total_sa)-100000 > 0){			echo "<div >EXCESO SOBRE GARANTIA:".(($total_en - $total_sa)-100000)."€</div>";		}    	?>    </div>    <div class="separador"></div>        <div id="salir">         <?php include("binicio.inc"); ?>    </div></div><!-- cierre div principal --></body></html>