rmat", "dd/mm/yy" );			$('#fechaent').datepicker('setDate', 'today');							$('#total').click(function(){				//$('#total').val((parseFloat($('#arancel').val()) + parseFloat($('#antidumping').val())) * ($('#iva').val()/100 + 1));$('#total').val( Math.round((parseFloat($('#arancel').val().replace(",",".")) + parseFloat($('#antidumping').val().replace(",",".")) + parseFloat($('#iiee').val().replace(",",".")) + parseFloat($('#iva').val().replace(",",".")))*100)/100);			});			});				</script></head><body><div id="principal">	<div class="titulo">    	ENTRADA DE CONTENEDORES    </div>    <div class="separador"></div>    <div class="formulario">    <?php		$comprobado="NO COMPROBADO";		$num1 = "";		$num2 = "";		$codigo = "";				if (isset($_POST['contenedor'])){			$num1 = $_POST['contenedor'];			$num2 = $_POST['digito'];			$codigo = $num1.'-'.$num2;			$sql="SELECT * FROM ctcontenedor WHERE codigo='$codigo'";			$resultado = mysql_query($sql);			$filas = mysql_num_rows($resultado);			if ($filas==FALSE){					echo "<script>alert('El contenedor introducido esta libre');</script>";					$comprobado="COMPROBADO";			}else{				echo "<script>alert('El contenedor introducido ya existe, introduce un numero distinto');</script>";				$num1 = "";				$num2 = "";				$codigo = "";			}				}?>    	 <form method="post" id="form" name="form" action="formentra.php" enctype="multipart/form-data">        	 <table class="tabla">        <tr>            <td >Contenedor:</td>            <td ><input name="contenedor" type="text" id="contenedor" value="<?php echo $num1; ?>" size="11" maxlength="10" onkeypress="keyUp(event)" onkeyup="checkDC(event)">-<input name="digito" type="text" id="digito" size="1" maxlength="1" readonly="TRUE" value="<?php echo $num2; ?>">                        </td>        </tr>        <tr>            <td>Codigo:</td>            <td  class="lf">            <input 